function  sumY_fg = cg2fg(Y, N, srf, method)        if strcmp(method, 'upscale')        sumY_fg = zeros(N*srf);        for k=1:N            for j=1:N                for t=1:srf                    for s=1:srf                        sumY_fg(srf*(k-1)+t,srf*(j-1)+s)=Y(k,j);                    end                end            end        end    elseif strcmp(method, 'cubic')        [X,Y] = meshgrid(1:N,1:N);        [Xq,Yq] = meshgrid(1/srf:1/srf:N,1/srf:1/srf:N);        sumY_fg = interp2(X,Y,Y,Xq,Yq,'cubic');        for i=1:srf-1            sumY_fg(:,i) = sumY_fg(:,srf);            sumY_fg(i,:) = sumY_fg(srf,:);        end    else        error('There is not this kind of interpolation')    end    end